mapper.select:
DECLARE
@code int,
@message nvarchar(max),
@outstr xml,
@instr nvarchar(max),
@sql nvarchar(200),
@function nvarchar(100)
set @function =#{method}
set @instr =  #{inStr}

BEGIN TRY
SET @sql = @function +' @instr,@outstr OUTPUT'
EXEC "sp_executesql" @sql,N'@instr nvarchar(max) ,@outstr xml OUTPUT',@instr ,@outstr OUTPUT
END TRY
BEGIN CATCH
set @message = ERROR_MESSAGE()
set @code = -1
set @outstr = '<code>-1</code><message>'+ @message+'</message>' 
END CATCH
select @outstr
